#include <iostream>
#include <string>

using namespace std;

bool vowelCount(string listItem);
bool doubleLetter(string listItem);
bool bannedLetters(string listItem);

int main()
{
    string list;
    bool vowels, dubLetter, banned;
    int numNice, numNaughty;
    string currStr;

    list = "";
    numNice = 0;
    numNaughty = 0;

    for (int i = 0; i < list.size(); i += 16) {
        currStr = "";
        currStr.append(list, i, i + 16);

        vowels = vowelCount(currStr);
        dubLetter = doubleLetter(currStr);
        banned = bannedLetters(currStr);
        if ((vowels == true) && (dubLetter == true) && (banned == true)) {
            numNice++;
        }
        else {
            numNaughty++;
        }
    }

    cout << numNice << endl;
    cout << numNaughty << endl;

    return 0;
}

// check for number of vowels
bool vowelCount(string listItem) {
    int count;
    count = 0;
    for (int i = 0; i < 16; i++) {
        if (listItem[i] == 'a' || listItem[i] == 'e' || listItem[i] == 'i' || listItem[i] == 'o' || listItem[i] == 'u') {
            count++;
        }
    }
    if (count >= 3) {
        return true;
    }
    else
        return false;
}
//check for presence of double letters side by side
bool doubleLetter(string listItem) {
    int count;
    count = 0;
    for (int i = 0; i < 16; i++) {
        if (listItem[i] == listItem[i + 1]) {
            count++;
        }
    }
    if (count > 0) {
        return true;
    }
    else
        return false;
}
//check to see if ab, cd, pq, or xy occur
bool bannedLetters(string listItem) {
    int count;
    count = 0;
    for (int i = 0; i < 16; i++) {
        if ((listItem[i] == 'a' && listItem[i + 1] == 'b') || (listItem[i] == 'c' && listItem[i + 1] == 'd') || (listItem[i] == 'p' && listItem[i + 1] == 'q') || (listItem[i] == 'x' && listItem[i + 1] == 'y')) {
            count++;
        }
    }
    if (count > 0) {
        return false;
    }
    else
        return true;
}